() => {
	const renderBar = (el, color, glow = false) => {
		if (!el) return;
         const match = el.textContent.match(/(\d+)\s*\/\s*(\d+)/);
         if (!match) return;

		const current = parseInt(match[1]);
		const max = parseInt(match[2]);
		const percent = Math.min((current / max) * 100, 100).toFixed(2);

		// Create bar container
		const bar = document.createElement('div');
		bar.style.cssText = `
			width: 100%;
			background: #2424247a;
			border-radius: 3px;
			overflow: hidden;
			height: 2px;
			margin: 4px 0 10px 0;
			position: relative;
			${glow ? `box-shadow: 0 0 3px ${color};` : ''}
		`;

		// Filled portion
		const fill = document.createElement('div');
		fill.style.cssText = `
			width: ${percent}%;
			background-color: ${color};
			height: 100%;
			position: absolute;
			top: 0;
			left: 0;
		`;

        const label = document.createElement("div");


		// Inject and clean
		el.textContent = '';
		bar.appendChild(fill);
		bar.appendChild(label);
		el.appendChild(bar);
	};

	const applyToAll = () => {
		document
          .querySelectorAll(".hp-line")
          .forEach((el) => renderBar(el, "#07da66ff", true));
        document
          .querySelectorAll(".exp-line")
          .forEach((el) => renderBar(el, "#f3ce53ff", true));  // EXP = blue + glow
	};

	const init = () => {
		const ctx = SillyTavern.getContext();
		ctx.eventSource.on('TRACKER_PREVIEW_ADDED', applyToAll);
		ctx.eventSource.on('TRACKER_PREVIEW_UPDATED', applyToAll);
	};

	const cleanup = () => {
		const ctx = SillyTavern.getContext();
		ctx.eventSource.off('TRACKER_PREVIEW_ADDED');
		ctx.eventSource.off('TRACKER_PREVIEW_UPDATED');
	};

	return { init, cleanup };
}
